mapper.select:
SELECT RULE_ID, RULE_NAME, RULE_TYPE, RULE_LANGUAGE, REGULAR_TYPE, REGULAR_MATCH, REGULAR_REPLACE, EXECUTE_RULE, EXECUTE_PRIORITY, REMARK, VALIDSTATUS, (SELECT GROUP_CONCAT(EXAMPLE_CONTENT, '\n') FROM RULE_TEST_EXAMPLE WHERE RULE_ID = A.RULE_ID) EXAMPLE_CONTENT FROM RULE_CONFIG A WHERE RULE_ID IN
#{item.ruleId}
AND VALIDSTATUS = 1 AND SHARE_KEY IS NULL

mapper.update:
UPDATE RULE_CONFIG
SET SHARE_KEY = #{shareKey}
WHERE RULE_ID = #{ruleId}
